                    drain();
                }

                triggeredTopics[numTriggeredTopics++] = iterator->terminatingWildcardChild;
                iterator->terminatingWildcardChild->triggered = true;
            }
        }

        /* Do we have a wildcard child? */
        if (iterator->wildcardChild) {
